$(document).ready(function(){$("input:checkbox.check-filter").change(function(){if($("input:checkbox.check-filter:checked").length===0){$(".card-div-category").show();return}var n=[];$("input:checkbox.check-filter").each(function(){$(this).is(":checked")&&n.push($(this).attr("data-category"))});$(".card-div-category").hide();n.forEach(function(n){$(".card-div-category[data-category='"+n+"']").show()});$("input:checkbox.check-filter:checked").length===0&&$(".card-div-category").show()})});